	if err != nil {
		span.SetStatus(otelcodes.Error, "Failed to check if user has points")
		span.RecordError(err)
		return 0, err
	}
	if !hasPoints {
		span.SetStatus(otelcodes.Ok, "User has no points")
		return 0, nil
	}

